import { Avatar, Button } from '@material-ui/core';
import React, { useEffect, useState } from 'react';
import '../../styles/Header.css';
import history from '../../misc/history';
import LoginService from '../../services/login.service';
import {MdPets} from "react-icons/md";
import ImageService from '../../services/image.service';


function Header() {
    const [imgSrc, setImgSrc] = useState<string | undefined>(undefined);

    useEffect(() => {
        ImageService.getUserImageAndSetIt(setImgSrc);
    }, []);

    return (
        <div>
            <header>
                <div className="nav">
                    <div className="homeicon">
                        <a href="/"><MdPets className="headerlogo"/></a>
                        <a href="/"><h1 className="title">Humanitarni šetači pasa</h1></a>
                    </div>
                    <label htmlFor="toggle" className="burgerbar">&#9776;</label>
                    <input type="checkbox" id="toggle"/>
                    <div className="menu">
                        <a href="/" ><Button className="btn" onClick={() => history.push("/")}>Home</Button></a>
                        <a href="/statistics" ><Button className="btn" onClick={() => history.push("/statistics")}>Statistika</Button></a>
                        <a href="#" ><Button className="btn" >O nama</Button></a>
                        { LoginService.isLoggedIn() ? (
                            <>
                                <a href="#" ><Button className="btn" onClick={() => history.push("/calendar")}>Raspored</Button></a>
                                <Button className="btn" onClick={() => history.push("/profile")}>Profil&nbsp;&nbsp;<Avatar src={`data:image/*;base64,${imgSrc}`} /></Button>
                                <Button className="btn" onClick={() => LoginService.logout()}>Logout</Button>
                            </>
                        ) : (
                            <>
                                <a href="/registration"><Button className="btn" onClick={() => history.push("/registration")}>Registracija</Button></a>
                                <a href="/login"><Button className="btn" onClick={() => history.push("/login")}>Login</Button></a>
                            </>
                        )}
                    </div>
                    

                </div>
            </header>
        </div>
    );
}

export default Header;
