import Button from '@material-ui/core/Button';
import TextField from '@material-ui/core/TextField';
import React, { useState } from 'react';
import { useForm } from 'react-hook-form';
import LoginInfo from '../../models/loginInfo.model';
import '../../styles/Login.css';
import LoginService from '../../services/login.service';
import { AxiosResponse } from 'axios';
import UserDetails from '../../models/userDetails.model';
import history from './../../misc/history';

function Login () {
   const [error, setError] = useState("");

   const { register, handleSubmit } = useForm<LoginInfo>();

   const onSubmit = (data: LoginInfo) => {
      console.log(data);

      LoginService.login(data.username, data.password).then((response: AxiosResponse) => {
         if (response === null) {
            setError("Wrong login information!");
         }
         else if (response.status === 401) {
            setError("Wrong login information!");
         }
         else if ((response as AxiosResponse<UserDetails>).status === 200) {
            history.push("/");
            window.location.reload(false);
         }
      }).catch(error => {
         setError(error);
      });
   }

    return (
        <div className="Login">
            <form  onSubmit={handleSubmit(onSubmit)}>
               <div className="horizontal-center">
                  <h1>Prijavite se:</h1>
               </div>
               <div className="horizontal-center">
                  {error}
               </div>
               <div className="horizontal-center">
                  <TextField id="username" inputRef={register} name="username" label="Korisničko ime" variant="outlined" />
               </div>
               <div className="horizontal-center">
                  <TextField id="password" type="password" inputRef={register} name="password" label="Lozinka" variant="outlined" />
               </div>
               <div className="horizontal-center">
                  <Button type="submit" variant="contained">Prijava</Button>
               </div>
            </form>
         </div>
      );
}

export default Login;
