import {addHours, format} from 'date-fns';
import React, {useEffect, useState} from 'react';
import CalendarEntry from '../../models/calendarEntry.model';
import CalendarService from '../../services/calendar.service';
import {Document, Font, Image, Page, PDFDownloadLink, Text, View} from '@react-pdf/renderer'
import CalendarView from '../advanced/calendar';
import DateUtils from '../../misc/date.utils';
import ImageService from '../../services/image.service';

function CalendarDoc(props: { entries: CalendarEntry[], dates: Date[] }) {
    Font.registerHyphenationCallback(word => [word]);

    return (

        <Document>
            <Page style={{padding: '50px'}}>
                {
                    props.dates.map(d => (
                        <View style={{paddingBottom: '30px'}}>
                            <Text style={{
                                paddingBottom: '10px',
                                color: 'gray',
                                fontSize: '12'
                            }}>{format(d, 'dd.MM.yyyy.')}</Text>
                            {
                                props.entries.filter(e => DateUtils.datesEqual(d, new Date(Date.parse(e.startTime))))
                                    .map(e => {
                                        const time = new Date(Date.parse(e.startTime));
                                        const timeDisplay = `${format(time, 'HH')}-${format(addHours(time, e.duration), 'HH')}`
                                        return (<View style={{paddingBottom: '10px', display: 'flex'}}>
                                            <View style={{width: 'auto'}}>
                                                <Text>{timeDisplay} sati</Text>
                                                {!!e.association && (
                                                    <Text style={{
                                                        color: 'gray',
                                                        fontSize: '12'
                                                    }}>{e.association.name} - {e.association.address}, {e.association.place}</Text>
                                                )}
                                                {!!e.citizen && (
                                                    <Text style={{
                                                        color: 'gray',
                                                        fontSize: '12'
                                                    }}>{e.citizen.firstName} {e.citizen.lastName}, {e.citizen.phoneNumber}</Text>
                                                )}
                                            </View>
                                            {
                                                e.animalIds.map(a => (
                                                    <View style={{padding: '3px'}}>
                                                        <Image style={{
                                                            width: '30px',
                                                            height: '30px',
                                                            borderRadius: '8',
                                                            objectFit: 'cover'
                                                        }} src={ImageService.getAnimalImageUrl(a)}/>
                                                    </View>
                                                ))
                                            }
                                        </View>)
                                    })
                            }
                        </View>
                    ))
                }
            </Page>
        </Document>
    );
}

function CalendarScreen() {
    const [data, setData] = useState<CalendarEntry[]>();
    const [dates, setDates] = useState<Date[]>();

    useEffect(() => {
        CalendarService.getCalendar().then(response => {
            setData(response.data);
            if (!!response.data && response.data.length > 0)
                setDates((response.data as CalendarEntry[])
                    .map(e => new Date(Date.parse(e.startTime)))
                    .map(t => new Date(t.getFullYear(), t.getMonth(), t.getDate()))
                    .filter((date, i, self) =>
                        self.findIndex(d => d.getTime() === date.getTime()) === i
                    )
                    .sort((a, b) => a.getTime() - b.getTime()));
        });
    }, []);

    return (
        <div>

            <br/>
                <div className="login">
                    {(!!data && !!dates) && (<div>
                            <PDFDownloadLink document={<CalendarDoc entries={data!} dates={dates!}/>}
                                             fileName="kalendar.pdf">
                                {({
                                      blob,
                                      url,
                                      loading,
                                      error
                                  }) => (loading ? 'Učitavanje dokumenta...' : 'Preuzmite raspored!')}
                            </PDFDownloadLink>
                        </div>
                    )}
                    <CalendarView entries={data} dates={dates}/>
                </div>
        </div>
    );
}

export default CalendarScreen;
