import Button from '@material-ui/core/Button';
import TextField from '@material-ui/core/TextField';
import React from 'react';
import AssociationRegistrationInfo from '../../models/associationRegistration.model';
import RegistrationService from '../../services/registration.service';
import { useForm, Controller } from 'react-hook-form';
import { MenuItem, Select } from '@material-ui/core';
import { useGetPlaces } from '../../services/place.service';
import '../../styles/Registration.css';
import history from './../../misc/history';

function AssociationRegistration() {
   const data = useGetPlaces();
   let errors = "";

   const { register, handleSubmit, control } = useForm<AssociationRegistrationInfo>();

   const onSubmit = (data: AssociationRegistrationInfo) => {
      console.log(data);

      RegistrationService.register(
         data
      ).then(response => {
         if (response.status === 200) {
            history.push("/");
         } else {
            errors = String(response.data);
         }
      });
   }

   return (
      <div>
         <div className="container">
            <form onSubmit={handleSubmit(onSubmit)}>
               <div className="horizontal-center">
                  <h1>Registracija udruge:</h1>
               </div>
               <div className="horizontal-center">
                  {errors}
               </div>
               <div className="horizontal-center">
                  <TextField id="username" inputRef={register} name="username" label="Korisničko ime" variant="outlined" />
               </div>
               <div className="horizontal-center">
                  <TextField id="email" type="email" inputRef={register} name="email" label="Email adresa" variant="outlined" />
               </div>
               <div className="horizontal-center">
                  <TextField id="password" type="password" inputRef={register} name="password" label="Lozinka" variant="outlined" />
               </div>
               <div className="horizontal-center">
                  <TextField id="name" inputRef={register} name="name" label="Naziv udruge" variant="outlined" />
               </div>
               <div className="horizontal-center">
                  <TextField id="phoneNumber" type="tel" inputRef={register} name="phoneNumber" label="Broj telefona udruge" variant="outlined" />
               </div>
               <div className="horizontal-center">
                  <TextField id="oib" inputRef={register} name="oib" label="OIB" variant="outlined" />
               </div>
               <div className="horizontal-center">
                  <TextField id="address" inputRef={register} name="address" label="Adresa" variant="outlined" />
               </div>
                <div className="horizontal-center">
                  <Controller
                     as={
                        <Select variant="outlined" label="mjesto">
                           {
                              data.map(place => 
                                 (<MenuItem key={place.id} value={place.id}>{place.name}</MenuItem>)
                              )
                           }
                        </Select>
                     }
                     defaultValue={""}
                     control={control}
                     name="place"
                     id="place"
                  />
               </div>

               <div className="horizontal-center">
                  <Button type="submit" variant="contained">Registracija</Button>
               </div>
            </form>
         </div>
      </div>
   );
}


export default AssociationRegistration;
