import React from 'react';
import {Card, CardActionArea, CardContent, CardMedia, makeStyles, Typography} from '@material-ui/core';
import Animal from '../../models/animal.model';
import ImageService from '../../services/image.service';
import clsx from 'clsx';
import CardActions from '@material-ui/core/CardActions';
import IconButton from '@material-ui/core/IconButton';
import FavoriteIcon from '@material-ui/icons/Favorite';
import ShareIcon from '@material-ui/icons/Share';

const useStyles = makeStyles({
    root: {
        display: 'inline-block',
        margin: '15px',
        minWidth: 275,
        maxWidth: 275,
        position: 'relative'
    },
    disabled: {
        filter: 'grayscale(100%)'
    },
    selected: {
        boxShadow: '0 4px 7px 3px rgba(0, 0, 0, .3)',
    },
    media: {
        height: 140,
    }
});

type AnimalCardProps = {
    animal: Animal,
    disabled?: boolean,
    onSelected?: Function
    selected: boolean
    setSelected?: Function
}

function AnimalCard(props: AnimalCardProps) {
    const disabled = !props.disabled ? false : true;

    const styles = useStyles();

    const gender = props.animal.gender === "MALE" ? "Muško" : "Žensko";

    const onClicked = () => {
        if (!disabled) {
            if (!!props.onSelected)
                props.onSelected(props.animal.id, !props.selected);
            return !props.selected;
        }
    }

    return (
        <Card
            className={`${styles.root} ${props.selected && !disabled ? styles.selected : ''} ${disabled ? styles.disabled : ''}`}
            variant="outlined">
            <CardActionArea onClick={event => {
                onClicked()
            }} disableRipple={disabled}>
                <CardMedia
                    className={styles.media} component='img' src={ImageService.getAnimalImageUrl(props.animal.id)}
                    onError={(e: any) => {
                        e.target.src = "/logo512.png"
                    }}
                />
                <CardContent>
                    <div className="animal-card-name-time">
                        <Typography variant="h5" component="h2">
                            {props.animal.name}
                        </Typography>
                        {/* <AccessAlarmIcon/>*/}
                    </div>
                    <Typography color="textSecondary" gutterBottom>
                        {props.animal.breed.breedName + ', ' + gender + ', ' + props.animal.birthYear}
                    </Typography>
                    <Typography color="textSecondary" gutterBottom>
                        {props.animal.description}
                    </Typography>
                </CardContent>
                <CardActions disableSpacing>
                    <IconButton aria-label="add to favorites">
                        <FavoriteIcon />
                    </IconButton>
                    <IconButton aria-label="share">
                        <ShareIcon />
                    </IconButton>
                    <IconButton
                        className={clsx(classes.expand, {
                            [classes.expandOpen]: expanded,
                        })}
                        onClick={handleExpandClick}
                        aria-expanded={expanded}
                        aria-label="show more"
                    >
                        <ExpandMoreIcon />
                    </IconButton>
                </CardActions>
                <Collapse in={expanded} timeout="auto" unmountOnExit>
                    <CardContent>
                        <Typography paragraph>Method:</Typography>
                        <Typography paragraph>
                            Heat 1/2 cup of the broth in a pot until simmering, add saffron and set aside for 10
                            minutes.
                        </Typography>
                        <Typography paragraph>
                            Heat oil in a (14- to 16-inch) paella pan or a large, deep skillet over medium-high
                            heat. Add chicken, shrimp and chorizo, and cook, stirring occasionally until lightly
                            browned, 6 to 8 minutes. Transfer shrimp to a large plate and set aside, leaving chicken
                            and chorizo in the pan. Add pimentón, bay leaves, garlic, tomatoes, onion, salt and
                            pepper, and cook, stirring often until thickened and fragrant, about 10 minutes. Add
                            saffron broth and remaining 4 1/2 cups chicken broth; bring to a boil.
                        </Typography>
                        <Typography paragraph>
                            Add rice and stir very gently to distribute. Top with artichokes and peppers, and cook
                            without stirring, until most of the liquid is absorbed, 15 to 18 minutes. Reduce heat to
                            medium-low, add reserved shrimp and mussels, tucking them down into the rice, and cook
                            again without stirring, until mussels have opened and rice is just tender, 5 to 7
                            minutes more. (Discard any mussels that don’t open.)
                        </Typography>
                        <Typography>
                            Set aside off of the heat to let rest for 10 minutes, and then serve.
                        </Typography>
                    </CardContent>
                </Collapse>
            </CardActionArea>
        </Card>
    );
}

export default AnimalCard;
