import React from 'react';
import '../../styles/Registration.css';
import {Button, Typography} from "@material-ui/core";
import {makeStyles} from "@material-ui/core/styles";
import LoginService from "../../services/login.service";
import history from "../../misc/history";
import Box from '@material-ui/core/Box';
import AssociationService from "../../services/association.service";
import {RouteComponentProps} from "react-router";
import '../../styles/Association.css';
import '../../styles/Global.css';
import imageService from "../../services/image.service";
import AnimalSelector from "../advanced/animalSelector";


type AssociationScreenProps = {
    id: string
}

const useStyles = makeStyles((theme) => ({
    margin: {
        margin: theme.spacing(1),
    },
    root: {
        width: 'fit-content',
        borderRadius: theme.shape.borderRadius,
        backgroundColor: theme.palette.background.paper,
        color: theme.palette.text.secondary,
    },
}));

const testPodaci = {"associationCount": 32, "walkCount": 525, "timeSpent": "17d14h53m", "animalsWalked": 134};

function ProfileScreen({match}: RouteComponentProps<AssociationScreenProps>) {

    const classes = useStyles();
    const [data, isFetching] = AssociationService.useGetAssociation(match.params.id);


    return (

        <div>
            {LoginService.isLoggedIn() ? (
                ((!isFetching && !!data) &&
                    <div className={"container-page"}>
                        <Box display="flex" flexWrap="wrap" alignItems="center" className={classes.root}>
                            <div className={"container-association"}>
                                <div>
                                    <img src={imageService.getAssociationImageUrl(match.params.id)} alt="" />
                                </div>
                                <div className={"association-name"}>
                                    <h1>
                                       {/* {data!.name}*/}
                                       Test imena
                                    </h1>
                                </div>
                                <div className={"association-address"}>
                                    <h3>
                                       {/* {data!.address}, {data!.place.name}*/}
                                       adresa
                                    </h3>
                                </div>
                            </div>
                            <AnimalSelector animals={data!.animals} associationId={match.params.id} />
                        </Box>
                    </div>)
            ) : (
                <>
                    <div className="center">
                        You are not logged in. If you already have account please
                        <a href="/login"><Button className="btn" onClick={() => history.push("/login")}>Log
                            in</Button></a>
                        or if you don't fell free to
                        <a href="/registration"><Button className="btn"
                                                        onClick={() => history.push("/registration")}>Register</Button></a>

                    </div>
                </>
            )}


        </div>
    );
}

export default ProfileScreen
